import React, { Component } from 'react';// on importe createContext qui servira à la création d'un ou plusieurs contextes
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import API from '../../utils/API';

var CanvasJSReact = require('./../../assets/canvasjs.react');
var CanvasJS = require('./../../assets/canvasjs.min');

var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

export class ShowActivity extends React.Component {
    constructor(props) {
        super(props);
        const {id} = props.match.params;
        this.state = {
            activitie : "",
            history : [],
            activity_id : id,
        }
        this.handleChange.bind(this);
        this.send.bind(this);


    }


    componentDidMount() {
        let currentComponent = this;
        API.checkToken(localStorage.getItem('token')).then(function(data){
            if(data.request.status === 200){
                console.log(currentComponent.state.activity_id);
                API.showActivity(currentComponent.state.activity_id).then(function (data) {
                    console.log(data)
                    currentComponent.setState({ activitie : data.data.activity })
                    currentComponent.setState({ history : data.data.activity.moods })
                },function (error) {
                    console.log(error);
                    return;
                })
            }
        },function (error) {
            console.log(error);
            return;
        })

    }

    send = event => {
        const currentComponent = this;
        var value = event.target.id;
        var activity = event.target.parentNode.id;
        var note = this.refs[activity].value;
        API.checkToken(localStorage.getItem('token')).then(function(data){
            if(data.request.status === 200){
                console.log(value,activity,note);
                API.createMood(value,activity,note).then(function (data) {
                    console.log(data);
                    currentComponent.componentDidMount();
                },function (error) {
                    console.log(error);
                    return;
                })
            }
        },function (error) {
            console.log(error);
            return;
        })

    }
    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }
    render() {
        const options = {
            animationEnabled: true,
            exportEnabled: true,
            theme: "light2", // "light1", "dark1", "dark2"
            title:{
                text: "Bounce Rate by Week of Year"
            },
            axisY: {
                title: "Bounce Rate",
                includeZero: false,
                suffix: "%"
            },
            axisX: {
                title: "Week of Year",
                prefix: "W",
                interval: 2
            },
            data: [{
                type: "line",
                toolTipContent: "Week {x}: {y}%",
                dataPoints: [
                    { x: 1, y: 64 },
                    { x: 2, y: 61 },
                    { x: 3, y: 64 },
                    { x: 4, y: 62 },
                    { x: 5, y: 64 },
                    { x: 6, y: 60 },
                    { x: 7, y: 58 },
                    { x: 8, y: 59 },
                    { x: 9, y: 53 },
                    { x: 10, y: 54 },
                    { x: 11, y: 61 },
                    { x: 12, y: 60 },
                    { x: 13, y: 55 },
                    { x: 14, y: 60 },
                    { x: 15, y: 56 },
                    { x: 16, y: 60 },
                    { x: 17, y: 59.5 },
                    { x: 18, y: 63 },
                    { x: 19, y: 58 },
                    { x: 20, y: 54 },
                    { x: 21, y: 59 },
                    { x: 22, y: 64 },
                    { x: 23, y: 59 }
                ]
            }]
        }
        return(
            <div>
            <div>
                <h1>{this.state.activitie.name}</h1>
                <ul id={this.state.activitie._id}>
                    <li id='1' onClick={this.send}>Happy</li>
                    <li id='2' onClick={this.send}>Not happy</li>
                    <li id='3' onClick={this.send}>Angry</li>
                    <textarea className="FormControl" ref={this.state.activitie._id} name="" id="" cols="30" rows="10"/>
               </ul>
            </div>
            <div>
            <h2>History</h2>
                <div>
                    <CanvasJSChart options = {options} />
                </div>
            <h2>History</h2>
                <ul>
                    {this.state.history.map((item,i) =>
                        <li key={i}>

                            <p>{new Date(item.created_at).toLocaleDateString()} - {item.value}</p>
                            <p>{item.note}</p>
                        </li>
                    ).reverse()}

                </ul>
            </div>
            </div>
    )
    }
}
