import React, { Component } from 'react';// on importe createContext qui servira à la création d'un ou plusieurs contextes
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import {Line} from 'react-chartjs-2';


import API from '../../utils/API';



export class ShowGroup extends React.Component {
    constructor(props) {
        super(props);
        const {id} = props.match.params;
        this.state = {
            group : "",
            activities : [],
            group_id : id,
        }
        this.handleChange.bind(this);
        this.send.bind(this);


    }


    componentDidMount() {
        let currentComponent = this;
        API.checkToken(localStorage.getItem('token')).then(function(data){
            if(data.request.status === 200){
                console.log(currentComponent.state.group_id);
                API.showGroup(currentComponent.state.group_id).then(function (data) {
                    console.log(data)
                    currentComponent.setState({ group : data.data.group })
                    currentComponent.setState({ activities : data.data.group.activity })
                },function (error) {
                    console.log(error);
                    return;
                })
            }
        },function (error) {
            console.log(error);
            return;
        })

    }

    send = event => {
        const currentComponent = this;
        var value = event.target.id;
        var activity = event.target.parentNode.id;
        var note = this.refs[activity].value;
        API.checkToken(localStorage.getItem('token')).then(function(data){
            if(data.request.status === 200){
                console.log(value,activity,note);
                API.createMood(value,activity,note).then(function (data) {
                    console.log(data);
                    currentComponent.componentDidMount();
                },function (error) {
                    console.log(error);
                    return;
                })
            }
        },function (error) {
            console.log(error);
            return;
        })

    }
    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }
    render() {
        {/*var values = [];*/}
        {/*var labels = [];*/}
        {/*this.state.history.map((item,i) =>*/}
           {/*values.push(item.value)*/}
        {/*)*/}
        {/*this.state.history.map((item,i) =>*/}
        {/*labels.push(new Date(item.created_at).toLocaleDateString())*/}
        {/*)*/}

        {/*const data = {*/}
            {/*labels: labels,*/}
            {/*datasets: [*/}
                {/*{*/}
                    {/*label: this.state.group.name,*/}
                    {/*fill: false,*/}
                    {/*lineTension: 0.1,*/}
                    {/*backgroundColor: 'rgba(75,192,192,0.4)',*/}
                    {/*borderColor: 'rgba(75,192,192,1)',*/}
                    {/*borderCapStyle: 'butt',*/}
                    {/*borderDash: [],*/}
                    {/*borderDashOffset: 0,*/}
                    {/*borderJoinStyle: 'miter',*/}
                    {/*pointBorderColor: 'rgba(75,192,192,1)',*/}
                    {/*pointBackgroundColor: '#fff',*/}
                    {/*pointBorderWidth: 1,*/}
        //             pointHoverRadius: 1,
        //             pointHoverBackgroundColor: 'rgba(75,192,192,1)',
        //             pointHoverBorderColor: 'rgba(220,220,220,1)',
        //             pointHoverBorderWidth: 2,
        //             pointRadius: 1,
        //             pointHitRadius: 3,
        //             data: values
        //         }
        //     ]
        // };

        return(
            <div>
            <div>
                <h1>{this.state.group.name}</h1>
                <ul id={this.state.group._id}>
                    <ul>
                        {this.state.activities.map((item,i) =>
                            <li key={i}>
                                <p>{new Date(item.created_at).toLocaleDateString()} - {item.name}</p>
                            </li>
                        ).reverse()}

                    </ul>
               </ul>
            </div>
            <div>
                <h2 className="col-12 text-center">Chart</h2>
                <div className="col-md-6 offset-md-3">
                    {/*<Line data={data}*/}
                          {/*options={{*/}
                              {/*scales: {*/}
                              {/*yAxes: [{*/}
                              {/*ticks: {*/}
                              {/*max: 4,*/}
                              {/*min: 0,*/}
                              {/*stepSize: 1*/}
                          {/*}*/}
                          {/*}]*/}
                          {/*},}}*/}
                          {/*width={50}*/}
                          {/*height={25} />*/}
                </div>
            </div>
            </div>
    )
    }
}
