import React, { createContext, Component } from 'react';// on importe createContext qui servira à la création d'un ou plusieurs contextes
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import API from '../../utils/API';


export class ShowActivity extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activitie : "",
        }
        this.handleChange.bind(this);
        this.send.bind(this);

        const {id} = props.match.params
        console.log(id)
    }


    componentDidMount() {
        let currentComponent = this;

    }

    send = event => {
        if(this.state.name.length === 0){
            return;
        }
        if(this.state.frequence.length === 0){
            return;
        }
        API.checkToken(localStorage.getItem('token')).then(function(data){
            if(data.request.status === 200){
                var value = event.target.id;
                var activity = event.target.parentNode.id;
                var note = this.refs[activity].value;
                console.log(value,activity,note);
                API.createMood(value,activity,note).then(function (data) {
                    console.log(data)
                },function (error) {
                    console.log(error);
                    return;
                })
            }
        },function (error) {
            console.log(error);
            return;
        })

    }
    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }
    render() {
        return(
            <li key={activitie._id}>
                <p>{activitie.name}</p>
                <ul  id={activitie._id} >
                    <li id="1" onClick={this.setMood}>Happy</li>
                    <li id="2" onClick={this.setMood}>Not happy</li>
                    <li id="3" onClick={this.setMood}>Hangry</li>
                    <textarea ref={activitie._id} htmlFor="note" />
                </ul>
            </li>
        )
    }
}
