import React from 'react';
import { Button } from "react-bootstrap";

import API from '../../utils/API';

export class Dashboard extends React.Component {
    constructor(props){
        super(props);
        this.disconnect.bind(this);
        this.state = {
            'activities' : [],
            'mood' : '',
            'username' : localStorage.getItem('username')
        };

        this.componentDidMount.bind(this);
        this.setMood.bind(this);
    }

    componentDidMount(){
        let currentComponent = this;
        API.checkToken(localStorage.getItem('token')).then(function(data){
            console.log(data.data.user._id);
            if(data.request.status === 200){
                API.indexActivity(data.data.user._id).then(function(data){
                    console.log(data.data);
                    currentComponent.setState({ activities : data.data.query })
                },function(error){
                    console.log(error);
                    return;
                })
            }else{
                console.log(data.request.status)
            }
        },function (error) {
            console.log(error);
            return;
        })
    }

    setMood = event => {
       API.createMood(event.target.id,event.target.parentNode.key).then(function (data) {
           console.log(data)
       })
    }

    disconnect = event => {
        API.logout();
        window.location = "/";
    }
    handleChange = event => {
        this.setState({
            [event.target.id]: event.target.value
        });
    }

    render() {
        const activities = this.state.activities;
        return(
            <div className="Dashboard col-md-8 justify-content-center">
                <h1>Hello {this.state.username}!</h1>
                <div className="col-md-8 text-left">

                    <ul>
                        {activities.map(activitie =>
                            <li key={activitie._id}>
                                <p>{activitie.name}</p>
                                <ul>
                                <li id="1" onClick={this.setMood}>Happy</li>
                                <li id="2" onClick={this.setMood}>Not happy</li>
                                <li id="3" onClick={this.setMood}>Hangry</li>
                                    <textarea htmlFor="note" />
                                </ul>
                            </li>
                        )}
                    </ul>
                </div>
                <Button
                    onClick={this.disconnect}
                    block
                    bsSize="large"
                    type="submit"
                >
                    Se déconnecter
                </Button>
            </div>
        )
    }
}
