import axios from 'axios';
const headers = {
    'Content-Type': 'application/json'
}
const burl = "http://localhost:8000"

export default {
    login : function(email,password) {
        return axios.post(burl + '/user/login',{
            'email' : email,
            'password' : password
        },{
            headers: headers
        })
    },
    signup : function(send){
        return axios.post(burl + '/user/signup',send,{headers: headers})
    },

    isAuth : function() {
        return (localStorage.getItem('token') !== null);
    },
    logout : function() {
        localStorage.clear();
    },
    checkToken : function(token) {
        return axios.post(burl + '/user/checkToken',{'token':token},{headers: headers})
    },
    createActivity : function(frequence,name,user_id) {
        return axios.post(burl + '/activity/create',{
            'frequence' : frequence,
            'name' : name,
            'user_id' : user_id,
            },
            {headers: headers})
    },
    indexActivity : function(user_id) {
        return axios.get(burl + '/activity/index',{
            params: {
                user_id: user_id
            }
        },
            {headers: headers})
    },
    indexGroup : function(user_id) {
        return axios.get(burl + '/group/index',{
            params: {
                user_id: user_id
            }
        },
            {headers: headers})
    },
    createGroup : function(name,user_id) {
        return axios.post(burl + '/group/create',
            {
                'name': name,
                'user_id': user_id
        },
            {headers: headers})
    },
    createMood : function(value,activity,note) {
        return axios.post(burl + '/mood/create',
            {
                'value': value,
                'note': note,
                'activity_id': activity
        },
            {headers: headers})
    },
    showActivity : function(activity) {
        return axios.get(burl + '/activity/show',{
                params: {
                    activity_id: activity
                }
            },
            {headers: headers})
    },
}
